<?class Assunto {	private $id				= "";	private $nome			= "";	private $usuario		= "";	private $data			= "";	private $disciplinas	= array();			public  $tmpDisciplinas		= array();	public  $tmpId				= "";	public  $tmpIdDisciplina	= "";	public  $tmpNome			= "";	public  $tmpUsuario			= "";	public  $tmpData			= "";	function Assunto(){		}	function getId(){		return $this->id;	}	function setId($id){		$this->id = $id;	}	function getUsuario(){		return $this->usuario;	}	function setUsuario($usuario){		$this->usuario= $usuario;	}	function getNome(){		return $this->nome;	}	function setNome($nome){		$this->nome = $nome;	}		function getData(){		return $this->data;	}		function setData($data){	$this->data = $data;	}		function addDisciplina($disciplina){		array_push($this->disciplinas,$disciplina);	}	function getQtdeDisciplina(){		return sizeof($this->disciplinas);	}	function getDisciplina($index){		return $this->disciplinas[$index];	}}?>