<?class BancodeDados {	private $host;	private $usuario;	private $senha;	private $conexao;	private $nome_banco;	public function BancodeDados() {				global $_host;		global $_user;		global $_senha;		global $_banco;		$this->host     	= $_host;		$this->usuario 		= $_user;		$this->senha		= $_senha;		$this->nome_banco   = $_banco;	}	public function conecta() {		$this->conexao = mysql_connect($this->host, $this->usuario,$this->senha);		mysql_select_db($this->nome_banco,$this->conexao);		return $this->conexao;	}	public function desconecta() {		mysql_close($this->conexao);	}	public function atualizaSQL($sql) {		$r = mysql_query($sql,$this->conexao);		if($r == 0) {			return false;		} else {			return true;		}	}	public function executaSQL($sql) {		$retorno = mysql_query($sql,$this->conexao);		return $retorno;	}	public function fetchArray($resultado) {		$retorno = mysql_fetch_array($resultado);		return $retorno;	}	public function numRows($resultado) {		return @mysql_num_rows($resultado);	}	public function erroNumero() {		return @mysql_errno($this->conexao);	}	public function insert_id() {		return @mysql_insert_id();	}	public function mysql_error() {		return @mysql_error();	}	public function iniciarTransacao() {		$this->executaSQL("begin");	}	public function efetivarTransacao() {		$this->executaSQL("commit");	}	public function desfazerTransacao() {		$this->executaSQL("rollback");	}}?>